ambience {
  modules {
    git-deploy {

      repositories {
        # specify key: value pairs where the value may be a remote git repository:    https://[user]:[pass]@github.com/[git repo].git like:
        # encypted to prevent user and pass from exposed

        # set up read only repository from github
        "remote":"https://github.com/ambience-cloud/ambience-repo-base.git"

        # or a local git repository (perhaps you cloned it earlier, or used git init to create)
        "local"="/opt/elixir/Ambience/git-repos/ambience-repo-base"
      }

      collections: [] 

      # Please reenable the default set of collections if you like to pull other info.
      #collections: ["ChainSets", "Dashboards","DashboardViews","Datasets","Forms","GISExplorer","GEOLayer","RecordEditor",
      # "Repository","RepositoryChunks", "SchedulerCalendars","SchedulerTriggers","Theme", "Visualisations", "Web", "WebStore", "Workflows", "Roles"]
    }
  }
}
